// Navigate back
// This is part of a "rocker" pair of actions where you roll your fingers
// across the right then left mouse buttons, or hold right and click left
// one or more times
// The description above assumes the right button is you stroke button
sp.SendVKey(vk.BROWSER_BACK);

// Navigate forward
// This is part of a "rocker" pair of actions where you roll your fingers
// across the left then right mouse buttons, or hold left and click right
// one or more times
// The description above assumes the right button is you stroke button
sp.SendVKey(vk.BROWSER_FORWARD);

// Maximize or restore the window underneath the mouse cursor where the gesture started
if(action.Window.Maximized) {
    action.Window.Restore();
} else {
    action.Window.Maximize();
}

//Test is the name of the public class exposed by TestPlugIn.dll in the Plug-Ins folder, MsgBox is a method of the Test class
//StrokesPlus will name the type the name of the class when it adds it into the script engine
//Download the source of the Test plug-in here: http://www.strokesplus.net/files/TestPlugIn.zip

var t = new Test();
t.MsgBox("Test", "This is from the TestPlugIn.dll plug-in.");

// Copies the area within the square gesture area to the clipboard, like a screenshot
// This is a complex action which cannot be reasonably accomplished in Steps

//Delay to ensure S+ has cleared the gesture and hint
sp.Sleep(100); 
//Create a new Bitmap in memory
var memoryImage = new drawing.System.Drawing.Bitmap(action.Bounds.Width, action.Bounds.Height);
//Create a graphics object associated with the bitmap
var memoryGraphics = drawing.System.Drawing.Graphics.FromImage(memoryImage);
//Copy the screen within the bounding rectangle of the drawn gesture area
//I used a square gesture since that seems more intuitive, but it's not neccessary
memoryGraphics.CopyFromScreen(action.Bounds.X, action.Bounds.Y, 0, 0, action.Bounds.Size);
//Copy the image to the clipboard
clip.SetImage(memoryImage);
memoryGraphics.Dispose();

//If you plan to use this image, for example via sp.DisplayImage, do not dispose 
//the object - comment/remove the line below - however, note that can lead to
//memory leaks - though the garbage collector should eventually finalize and dispose
memoryImage.Dispose();

//This will allow you to retain the expected functionality of holding the shift key and right clicking.
//Since this is an event that would be captured by S+, this action simply sends Shift+Right click directly
//This script operates under the assumption that the Shift key is still being held down when it executes

sp.ConsumePhysicalInput(true); //Suppress all mouse/keyboard input to ensure this action executes properly
sp.MouseClick(action.Start, MouseButtons.Right, true, true); //Click the right mouse button
sp.ConsumePhysicalInput(false); //Resume normal input

//An example of passing a string literal (non-escaped string) using: String.raw``
//so you don't have to escape things like backslashes, without using String.raw``
//you would need to use "C:\\Windows\\notepad.exe"
sp.RunProgram(String.raw`C:\Windows\notepad.exe`, "", "", "", false, true, false);

//Load and execute js from a file:
//this would load the .js file and execute it here, in the context
//of this script (and only in the current script engine)

eval(File.ReadAllText("C:\\Temp\\test.js"));

//Open text file and append a line to it:

var f = File.AppendText("C:\\Test\\test.js");
f.WriteLine("Test");
f.Close();

//Show a balloon tip of type Info in the lower right corner of the primary screen

//Types include: Warning, Error, Info, None
//Duration (5000 ms below) can be overridden by Windows Accessibility settings
sp.ShowBalloonTip("Title", "Message here", "Info", 5000);

//Show a message box with the names of the selected files in Explorer:

var files = sp.GetSelectedFilesInExplorer(action.Window.HWnd);
var str = "";
var delim = "";
for(i = 0; i < files.Length; i++)
{
    str = str + delim + files[i];
    delim = "\r\n";
}
if(str.length == 0) str = "(no files selected)";
sp.MessageBox(str, "Selected File(s)");

//	Get all open top-level windows, basically all running programs, and save the list to the clipboard:

var wnds = sp.AllApplications();
var apps = "";
for (var i = 0, len = wnds.Length; i < len; i++) {
    apps = apps + "Class: " + wnds[i].ClassName + " Title: " + wnds[i].Title + "\r\n";
}
clip.SetText(apps);

//Find all Notepad windows, move them to the gesture start location and resize to 400 x 400:

var matches = sp.WindowsFromTitleRegex("- Notepad$");
for (var i = 0, len = matches.Length; i < len; i++) {
    var wnd = matches[i];
    while (wnd.ParentSymmetric != null) {
        wnd = wnd.ParentSymmetric;
    }
    var pos = wnd.Position;
    pos.Left = action.Start.X;
    pos.Top = action.Start.Y;
    pos.Right = action.Start.X+400;
    pos.Bottom = action.Start.Y+400;
    wnd.Position = pos;
}

//Mute the default playback device:
sp.SendVKey(vk.VOLUME_MUTE);

//Another method:
//var mixer = sp.GetPlaybackMixer();
//mixer.ToggleMute();

//	Set the default playback device volume using the mixer device

var mixer = sp.GetPlaybackMixer();
mixer.SetVolumeAsync(37);

//Send an HTTP request to StackOverflow getting the most recent answers, then copy the resulting JSON to the clipboard:

var httpHandler = new HttpClientHandler();
httpHandler.AutomaticDecompression = host.flags(DecompressionMethods.GZip, DecompressionMethods.Deflate);
var client = new HttpClient(httpHandler);
client.BaseAddress = new Uri("https://api.stackexchange.com/2.2/");
var response = client.GetAsync("answers?order=desc&sort=activity&site=stackoverflow").Result;
var result = response.Content.ReadAsStringAsync().Result;

//Copy the resulting JSON to the clipboard
clip.SetText(result);

httpHandler.Dispose();
client.Dispose();

//Shows a popup menu with nested submenus and executes the corresponding script 
//when the menu item is clicked
var popupMenuInfoEx = new PopupMenuInfoEx(sp.GetCurrentMousePoint());
popupMenuInfoEx.MenuItems.Add(new PopupMenuItem("Test", `sp.MessageBox("Test", "Test");`));
popupMenuInfoEx.MenuItems.Add(new PopupMenuItem("-"));
var mnuSubMenu = new PopupMenuItem("Sub Menu");
    mnuSubMenu.SubMenuItems.Add(new PopupMenuItem("Sub Item 1", `sp.MessageBox("Sub Item 1", "Sub Item 1");`));
    var mnuSubSubMenu = new PopupMenuItem("Sub Sub Menu");
        mnuSubSubMenu.SubMenuItems.Add(new PopupMenuItem("Sub Sub Item 1", `sp.MessageBox("Sub Sub Item 1", "Sub Sub Item 1");`));
    mnuSubMenu.SubMenuItems.Add(mnuSubSubMenu);
    mnuSubMenu.SubMenuItems.Add(new PopupMenuItem("Sub Item 2", `sp.MessageBox("Sub Item 2", "Sub Item 2");`));
popupMenuInfoEx.MenuItems.Add(mnuSubMenu);
popupMenuInfoEx.MenuItems.Add(new PopupMenuItem("Last", `sp.MessageBox("Last", "Last");`));
popupMenuInfoEx.MenuItems.Add(new PopupMenuItem("-"));
popupMenuInfoEx.MenuItems.Add(new PopupMenuItem("Close Popup", ""));
sp.ShowPopupMenuEx(popupMenuInfoEx);

//Shows a window to collect input or provide a list from which to choose

var inputBoxInfo = new InputBoxInfo();
inputBoxInfo.Title = "Title text";
inputBoxInfo.Message = "Test";
//Call the line below for each item you want in the list
inputBoxInfo.Items.Add("Test Item 1");
inputBoxInfo.Items.Add("Test Item 2");
//Let the user manually type something in
inputBoxInfo.AllowDirectInput = true;
//Sort the items alphabetically
inputBoxInfo.Sort = true;
//Set the default item in the drop down list (optional)
inputBoxInfo.SelectedValue = "Test Item 2"; 
//Show the popup window at the mouse location and center the mouse 
//in the middle of the drop down list control
inputBoxInfo.ShowAtMouseCursor = true;
var res = sp.InputBox(inputBoxInfo);
if(res != null) {
    sp.MessageBox(res, "Result");
}

//Restricts mouse movement to only within the screen where the mouse is located

if(sp.MouseRestrictActive()) {
    //Restriction active, so let's remove it
    sp.MouseRestrictClear();
} else {
    //Create a rectangle and confine the mouse to that area
    var screenRect = Screen.FromPoint(sp.GetCurrentMousePoint()).Bounds;
    var rect = new Rectangle(screenRect.Location.X, screenRect.Location.Y, screenRect.Width - 1, screenRect.Height - 1);
    sp.MouseRestrictToRectangle(rect);
}

//Loop through all applications defined in StrokesPlus.net, show the message box for the app and each action:

for(i=0;i<sp_config.Applications.Count;i++){
    for(j=0;j<sp_config.Applications[i].Actions.Count;j++){
        sp.MessageBox(sp_config.Applications[i].Description + ": " + sp_config.Applications[i].Actions[j].Description, "App / Action");    
    }
}

//Loop through all global actions and show a message box:
for(j=0;j<sp_config.GlobalApplication.Actions.Count;j++){
    sp.MessageBox(sp_config.GlobalApplication.Actions[j].Description, "Action");    
}

//Loop through all methods in the sp (main S+ functions) class, building the name, parameters, 
//and return type then copy the list to the clipboard:

var spms = sp.GetMethods();
var s = "";
for(m=0;m<spms.Count();m++){
    s = s + "Method: " + spms[m].Name + "(";
    var comma = "";
    for(i=0;i<spms[m].GetParameters().Count();i++) {
        s = s + comma + spms[m].GetParameters()[i].ParameterType.ToString() + " " + spms[m].GetParameters()[i].Name
        comma = ", ";
    }
    s = s + ")\nReturns: " + spms[m].ReturnType.Name + "\n\n";
}
clip.SetText(s);

//Loop through all available language files in the Resources folder and present an input box to select changing:

//Get current culture
var culture = sp.GetCurrentCulture();

//Create input box to prompt selecting a language based on available files
var inputBoxInfo = new InputBoxInfo();
inputBoxInfo.Title = "Change Culture";
inputBoxInfo.Message = "Select an available language below. These are the languages available in the StrokesPlus.net\\Resources folder.";
var files = clr.System.IO.Directory.GetFiles(clr.System.IO.Path.GetDirectoryName(clr.System.Reflection.Assembly.GetEntryAssembly().Location) + "\\Resources");
for (i=0;i<files.Length;i++)
{
    var fi = new FileInfo(files[i]);
    var file = fi.Name;
    if(file.indexOf("Icons") !== 0) { //Ignore the Icons.resources file
        file = file.replace(/resources/ig , "").replace(/\./ig,"");
        if(file == "") file = "en-US"; //Since the culture name isn't in the English file name, add it manually
        inputBoxInfo.Items.Add(CultureInfo.GetCultureInfo(file).DisplayName + " [" + file + "]");
    }
}
inputBoxInfo.Sort = true;
//Show the input box
var res = sp.InputBox(inputBoxInfo);

//If a selection was made, change the culture
if(res != null) {
    var n = /\[(.*)\]/i; //Define the regular expression for matching the culture string
    var match = n.exec(res); //Get the text between the brackets, e.g. [en-US]
    sp.ChangeCulture(match[1]);
}

//Opens Microsoft Paint using the .NET Process class. This is the same as sp.RunProgram, but allows you to work with the classes directly if needed.

var start = new clr.System.Diagnostics.ProcessStartInfo();
// Enter in the command line arguments, everything you would enter after the executable name itself
start.Arguments = "";
// Enter the executable to run (or file if using UseShellExecute), including the complete path if not setting UseShellExecute = true
start.FileName = "mspaint.exe";
// Open in normal state, other options are .Hidden, .Maximized
start.WindowStyle = clr.System.Diagnostics.ProcessWindowStyle.Normal; 
// Verb is like the entry in the right-click menu of a file; "Open", "Edit", etc.
// Use "runas" for UAC/admin prompt, to run the program elevated
start.Verb = "";
start.CreateNoWindow = false;
// If below is true, it is like typing into the Window Run box, false requires a fully qualified path
start.UseShellExecute = true;
var exitCode = 0;
var waitForExit = false;
if (waitForExit) {
    // Run the external process & wait for it to finish
    var proc = clr.System.Diagnostics.Process.Start(start);
    proc.WaitForExit();
    // Retrieve the app's exit code
    exitCode = proc.ExitCode;
    // Do something here if you want to based on the exitCode value
    sp.MessageBox(exitCode, "Exit Code");
} else {
    clr.System.Diagnostics.Process.Start(start);
}

sp.SendWinDown();
sp.SendVKey(vk.TAB);
sp.SendWinUp();

//Increases the volume of the current playback device
//This is an action with no gesture, simply hold the stroke button
//and scroll the mouse wheel up while holding the stroke button
sp.SendVKey(vk.VOLUME_UP);

//Decreases the volume of the current playback device
